import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from "react-redux";
import { getGames, filterByGenre, filterByStored, sortByAlphabet, sortByRating, getGenres, filterByPlaforms } from '../redux/actions';
import { NavLink } from "react-router-dom";
import Card from './Card';
import Title from './Title';
import Footer from './Footer';
import Loader from './Loader';
import "./Home.css";
import Return from './Return';
import OrderByABC from './filters/OrderByABC';


export default function Home() {


    const [currentPage, setCurrentPage] = useState(0)
    const [search, setSearch] = useState("")
    document.title = "HenryGames"

    //globalStates

    const dispatch = useDispatch();
    const allGames = useSelector((state) => state.games)
    const genres = useSelector((state) => state.genres)


    //localStates


    //useEffect
    useEffect(() => {
        dispatch(getGames())
    }, [dispatch])

    useEffect(() => {
        dispatch(getGenres())
    }, [dispatch])

    //Paging
    const gamesPerPage = () => {
        if (search.length === 0) return allGames.slice(currentPage, currentPage + 15)

        const filteredGames = allGames.filter(game => game.name.toLowerCase().includes(search.toLowerCase()))
        return filteredGames.slice(currentPage, currentPage + 15)
    }

    const nextPage = () => {
        if (allGames.filter(game => game.name.includes(search)).length > currentPage + 15) {
            setCurrentPage(currentPage + 15)
        }
    }

    const prevPage = () => {
        if (currentPage > 0) {
            setCurrentPage(currentPage - 15)
        }
    }


    //filters&&Orders

    const [order, setOrder] = useState("")

    const handleGenreChange = (e) => {
        dispatch(filterByGenre(e.target.value))
        setCurrentPage(0)
    }

    const handleStoredChange = (e) => {
        dispatch(filterByStored(e.target.value))
        setCurrentPage(0)
    }

    const handleOrderA = (e) => {
        e.preventDefault();
        dispatch(sortByAlphabet(e.target.value))
        setOrder(e.target.value)
        setCurrentPage(0)
    }

    const handleOrderRating = (e) => {
        e.preventDefault();
        dispatch(sortByRating(e.target.value))
        setOrder(e.target.value)
        setCurrentPage(0)
    }

    const handlePlatformChange = (e) => {
        e.preventDefault();
        dispatch(filterByPlaforms(e.target.value))
        setCurrentPage(0)
    }


    //SearchBar & Refresh

    const handleClick = (e) => {
        e.preventDefault();
        dispatch(getGames())
        setCurrentPage(0)
    }

    const handleSearch = (e) => {
        setSearch(e.target.value)
        setCurrentPage(0)
    }


    return (
        <div>
            <div className='barsContainer'>
                <Title />


                <div className='second'>

                    <OrderByABC handleOrderA={handleOrderA} />


                    <div className='orderRatingContainer'>
                        <label>Order by Rating:
                            <select name='rating' onChange={handleOrderRating}>
                                <option disabled defaultValue="rating">Rating</option>
                                <option value="rating">Score</option>
                                <option value="high"> Highest Rated ★ </option>
                                <option value="low"> Lowest Rated ☆ </option>
                            </select>
                        </label>
                    </div>

                    <div>
                        <label> Search by Genre:
                            <select name='genre' defaultValue="all" onChange={handleGenreChange}>
                                <option disabled > Search by Genre: </option>
                                <option value="all"> All Genres </option>
                                {genres.map(g =>
                                    <option key={g.id} value={g.name}>
                                        {g.name}
                                    </option>
                                )}

                            </select>
                        </label>
                    </div>

                    <div className='filterPlatform' onChange={handlePlatformChange}>
                        <label>Games for:</label>
                        <select name='platform'>
                            <option disabled>PC Games:</option>
                            <option value="all">All Platforms</option>
                            <option value="PC">Windows</option>
                            <option value="Linux">Linux</option>
                            <option value="macOS">macOS</option>
                            <option disabled>Sony PlayStation:</option>
                            <option value="PlayStation 5">PlayStation 5</option>
                            <option value="PlayStation 4">PlayStation 4</option>
                            <option value="PlayStation 3">PlayStation 3</option>
                            <option value="PlayStation 2">PlayStation 2</option>
                            <option disabled>Microsoft Xbox:</option>
                            <option value="Xbox Series S/X"> Xbox Series S/X</option>
                            <option value="Xbox Series One"> Xbox Series One</option>
                            <option value="Xbox Series 360"> Xbox Series 360</option>
                            <option disabled>Mobile Games:</option>
                            <option value="Android"> Android</option>
                            <option value="iOS"> iOS</option>
                            <option value="Nintendo Switch"> Nintendo Switch</option>
                            <option value="PS Vita">PS Vita</option>


                        </select>
                    </div>


                    <div className='filterContainer'>
                        <label> Stored in:
                            <select name='stored' onChange={handleStoredChange}>
                                <option value="all" >Anywhere</option>
                                <option value="api">RAWG</option>
                                <option value="db">Created by Users!</option>
                            </select>
                        </label>
                    </div>




                </div>


                <div className='third'>

                    {/* //Paging && Refresh */}

                    <button className='pagingButton' onClick={prevPage}>⇐  Previous Page </button>

                    <button className='pagingButton'>
                        <NavLink className="createButton" to="/createGame"
                            style={() => ({
                                textDecoration: "none"

                            })}> Add Game to our Library!
                        </NavLink>
                    </button>

                    <button className='pagingButton' onClick={handleClick}> Refresh Games ↺</button>

                    <button className='pagingButton' onClick={nextPage}>Next Page ⇒ </button>

                </div>

                {/* SearchBar */}
                <input type="text" className="searchBar" placeholder="Search Games.." value={search} onChange={handleSearch} />
            </div>


            <div className="fourth">


                {
                    !gamesPerPage()[0] ?
                        <Loader /> :
                        allGames === "error" ?
                            <Return /> :


                            gamesPerPage()?.map(game => {

                                return (

                                    <div key={game.id} className="gridGame">

                                        <NavLink to={`/home/${game.id}`}
                                            style={({ isActive }) =>
                                            ({
                                                color: isActive ? "yellow" : "#fff",
                                                backgroundColor: "000",
                                                textDecoration: "none",
                                                borderRadius: "15px",
                                            })}>

                                            <Card
                                                id={game.id}
                                                image={game.image}
                                                name={game.name}
                                                genres={game.genres}
                                                rating={game.rating}
                                                platforms={game.platforms + "  "}
                                            />
                                        </NavLink>
                                    </div>
                                )
                            })

                }
            </div>
            <br></br>
            <Footer />

        </div>
    )
}
